gsap.registerPlugin(ScrollTrigger);

gsap.to("img", {opacity: 1,delay: 0.1}) // gentle fade in

let iteration = 0; // gets iterated when we scroll all the way to the end or start and wraps around - allows us to smoothly continue the playhead scrubbing in the correct direction.

const spacing = 0.1,    // spacing of the cards (stagger)
	snap = gsap.utils.snap(spacing), // we'll use this to snap the playhead on the seamlessLoop
	cards = gsap.utils.toArray('.cards li'),
	seamlessLoop = buildSeamlessLoop(cards, spacing),
	scrub = gsap.to(seamlessLoop, { // we reuse this tween to smoothly scrub the playhead on the seamlessLoop
		totalTime: 0,
		duration: 0.5,
		ease: "power3",
		paused: true
	}),
	trigger = ScrollTrigger.create({
		start: 0,
		onUpdate(self) {
			if (self.progress === 1 && self.direction > 0 && !self.wrapping) {
				wrapForward(self);
			} else if (self.progress < 1e-5 && self.direction < 0 && !self.wrapping) {
				wrapBackward(self);
			} else {
        scrub.vars.totalTime = snap((iteration + self.progress) * seamlessLoop.duration());
				scrub.invalidate().restart(); // to improve performance, we just invalidate and restart the same tween. No need for overwrites or creating a new tween on each update.
				self.wrapping = false;
			}
		},
		end: "+=3000",
		pin: ".gallery"
	});

function wrapForward(trigger) { // when the ScrollTrigger reaches the end, loop back to the beginning seamlessly
	iteration++;
	trigger.wrapping = true;
	trigger.scroll(trigger.start + 1);
}

function wrapBackward(trigger) { // when the ScrollTrigger reaches the start again (in reverse), loop back to the end seamlessly
	iteration--;
	if (iteration < 0) { // to keep the playhead from stopping at the beginning, we jump ahead 10 iterations
		iteration = 9;
		seamlessLoop.totalTime(seamlessLoop.totalTime() + seamlessLoop.duration() * 10);
    scrub.pause(); // otherwise it may update the totalTime right before the trigger updates, making the starting value different than what we just set above. 
	}
	trigger.wrapping = true;
	trigger.scroll(trigger.end - 1);
}

function scrubTo(totalTime) { // moves the scroll position to the place that corresponds to the totalTime value of the seamlessLoop, and wraps if necessary.
	let progress = (totalTime - seamlessLoop.duration() * iteration) / seamlessLoop.duration();
  console.log(progress)
	if (progress > 1) {
		wrapForward(trigger);
	} else if (progress < 0) {
		wrapBackward(trigger);
	} else {
		trigger.scroll(trigger.start + progress * (trigger.end - trigger.start));
	}
}

document.querySelector(".next").addEventListener("click", () => scrubTo(scrub.vars.totalTime + spacing));
document.querySelector(".prev").addEventListener("click", () => scrubTo(scrub.vars.totalTime - spacing));


function buildSeamlessLoop(items, spacing) {
	let overlap = Math.ceil(1 / spacing), // number of EXTRA animations on either side of the start/end to accommodate the seamless looping
		startTime = items.length * spacing + 0.5, // the time on the rawSequence at which we'll start the seamless loop
		loopTime = (items.length + overlap) * spacing + 1, // the spot at the end where we loop back to the startTime 
		rawSequence = gsap.timeline({paused: true}), // this is where all the "real" animations live
		seamlessLoop = gsap.timeline({ // this merely scrubs the playhead of the rawSequence so that it appears to seamlessly loop
			paused: true,
			repeat: -1, // to accommodate infinite scrolling/looping
			onRepeat() { // works around a super rare edge case bug that's fixed GSAP 3.6.1
				this._time === this._dur && (this._tTime += this._dur - 0.01);
			}
		}),
		l = items.length + overlap * 2,
		time = 0,
		i, index, item;

	// set initial state of items
	gsap.set(items, {xPercent: 400, opacity: 0,	scale: 0});

	// now loop through and create all the animations in a staggered fashion. Remember, we must create EXTRA animations at the end to accommodate the seamless looping.
	for (i = 0; i < l; i++) {
		index = i % items.length;
		item = items[index];
		time = i * spacing;
		rawSequence.fromTo(item, {scale: 0, opacity: 0}, {scale: 1, opacity: 1, zIndex: 100, duration: 0.5, yoyo: true, repeat: 1, ease: "power1.in", immediateRender: false}, time)
		           .fromTo(item, {xPercent: 400}, {xPercent: -400, duration: 1, ease: "none", immediateRender: false}, time);
		i <= items.length && seamlessLoop.add("label" + i, time); // we don't really need these, but if you wanted to jump to key spots using labels, here ya go.
	}
	
	// here's where we set up the scrubbing of the playhead to make it appear seamless. 
	rawSequence.time(startTime);
	seamlessLoop.to(rawSequence, {
		time: loopTime,
		duration: loopTime - startTime,
		ease: "none"
	}).fromTo(rawSequence, {time: overlap * spacing + 1}, {
		time: startTime,
		duration: startTime - (overlap * spacing + 1),
		immediateRender: false,
		ease: "none"
	});
	return seamlessLoop;
}

document.addEventListener("DOMContentLoaded", () => {
	const sliders = document.querySelectorAll(".emotions-slider");

	if (!sliders.length) return;

	const list = [];

	sliders.forEach((element) => {
		const [slider, prevEl, nextEl, pagination] = [
			element.querySelector(".swiper"),
			element.querySelector(".slider-nav__item_prev"),
			element.querySelector(".slider-nav__item_next"),
			element.querySelector(".slider-pagination")
		];

		list.push(
			new Swiper(slider, {
				slidesPerView: "auto",
				spaceBetween: 20,
				speed: 600,
				observer: true,
				watchOverflow: true,
				watchSlidesProgress: true,
				centeredSlides: true,
				initialSlide: 1,
				navigation: { nextEl, prevEl, disabledClass: "disabled" },
				pagination: {
					el: pagination,
					type: "bullets",
					modifierClass: "slider-pagination",
					bulletClass: "slider-pagination__item",
					bulletActiveClass: "active",
					clickable: true
				},
				breakpoints: {
					768: { spaceBetween: 40 }
				}
			})
		);
	});
});

import React from 'react';
import { AnimatePresence, motion } from 'framer-motion';
import { useTranslation } from 'react-i18next';
import { X } from 'lucide-react';

const STORAGE_KEY = 'commerzio_cookie_prefs';
const ANALYTICS_KEY = 'commerzio_analytics_log';

const defaultPrefs = {
    necessary: true,
    analytics: false,
    marketing: false
};

const CookieConsent = () => {
    const { t } = useTranslation();
    const MotionDiv = motion.div;
    const [storageAvailable, setStorageAvailable] = React.useState(true);
    const [prefs, setPrefs] = React.useState(defaultPrefs);
    const [showBanner, setShowBanner] = React.useState(false);
    const [showManage, setShowManage] = React.useState(false);
    const [showData, setShowData] = React.useState(false);
    const [analyticsData, setAnalyticsData] = React.useState([]);

    React.useEffect(() => {
        try {
            const testKey = '__cookie_test__';
            window.localStorage.setItem(testKey, testKey);
            window.localStorage.removeItem(testKey);
            setStorageAvailable(true);
        } catch {
            setStorageAvailable(false);
        }
    }, []);

    React.useEffect(() => {
        if (!storageAvailable) return;
        try {
            const stored = window.localStorage.getItem(STORAGE_KEY);
            if (stored) {
                const parsed = JSON.parse(stored);
                setPrefs({ ...defaultPrefs, ...parsed, necessary: true });
            } else {
                setShowBanner(true);
            }
        } catch {
            setPrefs(defaultPrefs);
            setShowBanner(true);
        }
    }, [storageAvailable]);

    React.useEffect(() => {
        const handleOpenSettings = () => {
            setShowManage(true);
        };
        window.addEventListener('open-cookie-settings', handleOpenSettings);
        return () => window.removeEventListener('open-cookie-settings', handleOpenSettings);
    }, []);

    React.useEffect(() => {
        if (!prefs.analytics || !storageAvailable) return;
        try {
            const entry = {
                path: window.location.pathname,
                timestamp: new Date().toISOString(),
                referrer: document.referrer || 'direct'
            };
            const existing = JSON.parse(window.localStorage.getItem(ANALYTICS_KEY) || '[]');
            const next = [entry, ...existing].slice(0, 10);
            window.localStorage.setItem(ANALYTICS_KEY, JSON.stringify(next));
        } catch {
            // Ignore analytics storage errors
        }
    }, [prefs.analytics]);

    React.useEffect(() => {
        if (!showData || !storageAvailable) return;
        try {
            const stored = JSON.parse(window.localStorage.getItem(ANALYTICS_KEY) || '[]');
            setAnalyticsData(stored);
        } catch {
            setAnalyticsData([]);
        }
    }, [showData]);

    const persistPrefs = (nextPrefs) => {
        const normalized = { ...nextPrefs, necessary: true };
        setPrefs(normalized);
        if (!storageAvailable) return;
        try {
            window.localStorage.setItem(STORAGE_KEY, JSON.stringify(normalized));
        } catch {
            // Ignore storage errors (private mode, blocked storage)
        }
    };

    const handleAccept = () => {
        persistPrefs({ ...prefs, analytics: true, marketing: true });
        setShowBanner(false);
    };

    const handleReject = () => {
        persistPrefs({ ...prefs, analytics: false, marketing: false });
        setShowBanner(false);
    };

    const handleSave = () => {
        persistPrefs(prefs);
        setShowManage(false);
        setShowBanner(false);
    };

    if (!storageAvailable) {
        return null;
    }

    return (
        <>
            <AnimatePresence>
                {showBanner && (
                    <MotionDiv
                        className="cookie-banner"
                        initial={{ opacity: 0, y: 30 }}
                        animate={{ opacity: 1, y: 0 }}
                        exit={{ opacity: 0, y: 30 }}
                    >
                        <div className="cookie-banner__text">
                            <h4>{t('cookie_title')}</h4>
                            <p>{t('cookie_body')}</p>
                        </div>
                        <div className="cookie-banner__actions">
                            <button type="button" className="cookie-btn" onClick={() => setShowManage(true)}>
                                {t('cookie_manage')}
                            </button>
                            <button type="button" className="cookie-btn" onClick={handleReject}>
                                {t('cookie_reject')}
                            </button>
                            <button type="button" className="cookie-btn cookie-btn--primary" onClick={handleAccept}>
                                {t('cookie_accept')}
                            </button>
                        </div>
                    </MotionDiv>
                )}
            </AnimatePresence>

            <AnimatePresence>
                {showManage && (
                    <MotionDiv
                        className="cookie-modal"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        onClick={() => setShowManage(false)}
                    >
                        <MotionDiv
                            className="cookie-modal__content"
                            initial={{ opacity: 0, y: 16, scale: 0.96 }}
                            animate={{ opacity: 1, y: 0, scale: 1 }}
                            exit={{ opacity: 0, y: 16, scale: 0.96 }}
                            onClick={(event) => event.stopPropagation()}
                        >
                            <div className="cookie-modal__header">
                                <h3>{t('cookie_settings_title')}</h3>
                                <button type="button" className="cookie-modal__close" onClick={() => setShowManage(false)}>
                                    <X size={18} />
                                </button>
                            </div>
                            <p>{t('cookie_settings_body')}</p>
                            <div className="cookie-toggle">
                                <div>
                                    <strong>{t('cookie_necessary_title')}</strong>
                                    <span>{t('cookie_necessary_body')}</span>
                                </div>
                                <input type="checkbox" checked disabled aria-label={t('cookie_necessary_title')} />
                            </div>
                            <div className="cookie-toggle">
                                <div>
                                    <strong>{t('cookie_analytics_title')}</strong>
                                    <span>{t('cookie_analytics_body')}</span>
                                </div>
                                <input
                                    type="checkbox"
                                    checked={prefs.analytics}
                                    onChange={(event) => setPrefs((prev) => ({ ...prev, analytics: event.target.checked }))}
                                    aria-label={t('cookie_analytics_title')}
                                />
                            </div>
                            <div className="cookie-toggle">
                                <div>
                                    <strong>{t('cookie_marketing_title')}</strong>
                                    <span>{t('cookie_marketing_body')}</span>
                                </div>
                                <input
                                    type="checkbox"
                                    checked={prefs.marketing}
                                    onChange={(event) => setPrefs((prev) => ({ ...prev, marketing: event.target.checked }))}
                                    aria-label={t('cookie_marketing_title')}
                                />
                            </div>
                            <div className="cookie-banner__actions">
                                <button type="button" className="cookie-btn" onClick={() => setShowData(true)}>
                                    {t('cookie_view_data')}
                                </button>
                                <button type="button" className="cookie-btn cookie-btn--primary" onClick={handleSave}>
                                    {t('cookie_save')}
                                </button>
                            </div>
                        </MotionDiv>
                    </MotionDiv>
                )}
            </AnimatePresence>

            <AnimatePresence>
                {showData && (
                    <MotionDiv
                        className="cookie-modal"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        onClick={() => setShowData(false)}
                    >
                        <MotionDiv
                            className="cookie-modal__content"
                            initial={{ opacity: 0, y: 16, scale: 0.96 }}
                            animate={{ opacity: 1, y: 0, scale: 1 }}
                            exit={{ opacity: 0, y: 16, scale: 0.96 }}
                            onClick={(event) => event.stopPropagation()}
                        >
                            <div className="cookie-modal__header">
                                <h3>{t('cookie_data_title')}</h3>
                                <button type="button" className="cookie-modal__close" onClick={() => setShowData(false)}>
                                    <X size={18} />
                                </button>
                            </div>
                            <p>{t('cookie_data_body')}</p>
                            <div className="cookie-data">
                                {analyticsData.length === 0 ? (
                                    <div>{t('cookie_data_empty')}</div>
                                ) : (
                                    analyticsData.map((entry, index) => (
                                        <div key={`${entry.timestamp}-${index}`}>
                                            <strong>{entry.path}</strong> ┬À {entry.referrer} ┬À {new Date(entry.timestamp).toLocaleString()}
                                        </div>
                                    ))
                                )}
                            </div>
                            <div className="cookie-banner__actions">
                                <button type="button" className="cookie-btn cookie-btn--primary" onClick={() => setShowData(false)}>
                                    {t('cookie_close')}
                                </button>
                            </div>
                        </MotionDiv>
                    </MotionDiv>
                )}
            </AnimatePresence>
        </>
    );
};

export default CookieConsent;

import React from 'react';
import { useTranslation } from 'react-i18next';
import { Linkedin, Mail, X } from 'lucide-react';
import { AnimatePresence, motion } from 'framer-motion';
import ContactForm from './ContactForm';

const Footer = () => {
    const { t } = useTranslation();
    const [isContactOpen, setIsContactOpen] = React.useState(false);
    const [activeLegal, setActiveLegal] = React.useState(null);

    // Esc key handler for legal modal
    React.useEffect(() => {
        const handleEsc = (event) => {
            if (event.key === 'Escape') {
                if (activeLegal) setActiveLegal(null);
            }
        };
        if (activeLegal) {
            document.addEventListener('keydown', handleEsc);
        }
        return () => document.removeEventListener('keydown', handleEsc);
    }, [activeLegal]);

    const legalContent = React.useMemo(() => ({
        terms: {
            title: t('legal_terms_title'),
            sections: [
                {
                    heading: t('legal_terms_scope_title'),
                    content: t('legal_terms_scope_content')
                },
                {
                    heading: t('legal_terms_services_title'),
                    content: t('legal_terms_services_content')
                },
                {
                    heading: t('legal_terms_ip_title'),
                    content: t('legal_terms_ip_content')
                },
                {
                    heading: t('legal_terms_liability_title'),
                    content: t('legal_terms_liability_content')
                },
                {
                    heading: t('legal_terms_jurisdiction_title'),
                    content: t('legal_terms_jurisdiction_content')
                }
            ]
        },
        privacy: {
            title: t('legal_privacy_title'),
            sections: [
                {
                    heading: t('legal_privacy_controller_title'),
                    content: t('legal_privacy_controller_content')
                },
                {
                    heading: t('legal_privacy_data_title'),
                    content: t('legal_privacy_data_content')
                },
                {
                    heading: t('legal_privacy_purpose_title'),
                    content: t('legal_privacy_purpose_content')
                },
                {
                    heading: t('legal_privacy_rights_title'),
                    content: t('legal_privacy_rights_content')
                },
                {
                    heading: t('legal_privacy_retention_title'),
                    content: t('legal_privacy_retention_content')
                }
            ]
        },
        cookies: {
            title: t('legal_cookies_title'),
            sections: [
                {
                    heading: t('legal_cookies_what_title'),
                    content: t('legal_cookies_what_content')
                },
                {
                    heading: t('legal_cookies_types_title'),
                    content: t('legal_cookies_types_content')
                },
                {
                    heading: t('legal_cookies_manage_title'),
                    content: t('legal_cookies_manage_content')
                }
            ],
            action: () => {
                window.dispatchEvent(new Event('open-cookie-settings'));
                setActiveLegal(null);
            },
            actionLabel: t('legal_cookies_action')
        }
    }), [t]);

    React.useEffect(() => {
        const footerContent = document.querySelector('.footer-content');
        if (!footerContent) return;

        const animateElements = () => {
            const elements = footerContent.children;
            const animations = ['footer-fade-slide', 'footer-bounce', 'footer-scale-pulse', 'footer-rotate-subtle'];

            Array.from(elements).forEach((element) => {
                element.classList.remove(...animations);
                const randomAnimation = animations[Math.floor(Math.random() * animations.length)];
                element.classList.add(randomAnimation);
            });
        };

        animateElements();
        const interval = setInterval(animateElements, 4000);
        return () => clearInterval(interval);
    }, []);

    return (
        <footer className="app-footer">
            <div className="footer-content footer-grid">
                <div className="footer-brand">
                    <h4 className="logo" style={{ marginBottom: '0.5rem' }}>{t('agency_name')}</h4>
                    <p className="footer-tagline">{t('slogan')}</p>
                    <p className="footer-summary">{t('footer_summary')}</p>
                    <div className="social-links">
                        <a href="https://www.linkedin.com/in/chrislazar93/" target="_blank" rel="noopener noreferrer" aria-label="LinkedIn">
                            <Linkedin size={22} />
                        </a>
                        <a href="mailto:sales@commerzio.online" aria-label="Email">
                            <Mail size={22} />
                        </a>
                    </div>
                </div>

                <div className="footer-column">
                    <h5>{t('footer_contact_title')}</h5>
                    <p>{t('footer_contact_lead')}</p>
                    <button type="button" className="footer-contact-btn" onClick={() => setIsContactOpen(true)}>
                        {t('footer_contact_cta')}
                    </button>
                </div>

                <div className="footer-column">
                    <h5>{t('footer_legal_title')}</h5>
                    <div className="footer-legal-links">
                        <button type="button" className="footer-legal-btn" onClick={() => setActiveLegal('terms')}>
                            {t('footer_terms_title')}
                        </button>
                        <button type="button" className="footer-legal-btn" onClick={() => setActiveLegal('privacy')}>
                            {t('footer_privacy_title')}
                        </button>
                        <button type="button" className="footer-legal-btn" onClick={() => setActiveLegal('cookies')}>
                            {t('footer_cookies_title')}
                        </button>
                    </div>
                </div>

                <div className="footer-column footer-right">
                    <p className="footer-note">{t('product_by')}</p>
                    <p className="footer-rights">{t('footer_rights')}</p>
                </div>
            </div>
            <AnimatePresence>{isContactOpen && <ContactForm onClose={() => setIsContactOpen(false)} />}</AnimatePresence>
            <AnimatePresence>
                {activeLegal && (
                    <motion.div
                        className="legal-modal legal-modal--wide"
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        onClick={() => setActiveLegal(null)}
                    >
                        <motion.div
                            className="legal-modal__content legal-modal__content--wide"
                            initial={{ opacity: 0, y: 20, scale: 0.95 }}
                            animate={{ opacity: 1, y: 0, scale: 1 }}
                            exit={{ opacity: 0, y: 20, scale: 0.95 }}
                            transition={{ duration: 0.25 }}
                            onClick={(event) => event.stopPropagation()}
                        >
                            <div className="legal-modal__header">
                                <h3>{legalContent[activeLegal].title}</h3>
                                <button type="button" className="legal-modal__close" onClick={() => setActiveLegal(null)}>
                                    <X size={20} />
                                </button>
                            </div>
                            <div className="legal-modal__body">
                                {legalContent[activeLegal].sections.map((section, index) => (
                                    <div key={index} className="legal-section">
                                        <h4>{section.heading}</h4>
                                        <p>{section.content}</p>
                                    </div>
                                ))}
                            </div>
                            {legalContent[activeLegal].action && (
                                <div className="legal-modal__footer">
                                    <button type="button" className="legal-modal__action" onClick={legalContent[activeLegal].action}>
                                        {legalContent[activeLegal].actionLabel}
                                    </button>
                                </div>
                            )}
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>
        </footer>
    );
};

export default Footer;
